function setCookie(b,c,a){var d=new Date();d.setDate(d.getDate()+a);document.cookie=b+"="+escape(c)+((a==null)?"":";expires="+d.toGMTString())+";path=/"}function getCookie(a){if(document.cookie.length>0){c_start=document.cookie.indexOf(a+"=");if(c_start!=-1){c_start=c_start+a.length+1;c_end=document.cookie.indexOf(";",c_start);if(c_end==-1){c_end=document.cookie.length}return unescape(document.cookie.substring(c_start,c_end))}}return""}function getCss(a){cssid=getCookie(a);if(cssid!=null&&cssid!=""){return cssid}else{return 0}}function changeCss(a,c){if(c>=0&&c<10){for(var b=0;b<10;b++){if(b==c){document.getElementById("CSS"+b).disabled=false;setCookie(a,b,365)}else{document.getElementById("CSS"+b).disabled=true}}}}function selectCss(a){changeCss(a,document.getElementById("select-css").value)};
